import React, { useState, useEffect } from 'react';
import styles from './LogosSection.module.css';

const LogosSection = () => {
  const [logos, setLogos] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Realizar llamada a la API para obtener logos desde el backend
    const fetchLogos = async () => {
      try {
        setIsLoading(true);
        // Usar la ruta relativa al backend. Este endpoint es público.
        const response = await fetch('/api/admin/get_brands');
        const result = await response.json();

        if (result.success) {
          // La API devuelve los logos en result.data
          setLogos(result.data);
          setError(null); // Limpiar cualquier error previo
        } else {
          // Manejar caso de error si la API devuelve success: false
          setError(new Error(result.msg || 'Error fetching logos'));
          setLogos([]); // Limpiar logos en caso de error
        }
      } catch (err) {
        // Manejar errores de red o de la petición
        console.error('Error fetching logos:', err);
        setError(err);
        setLogos([]); // Limpiar logos en caso de error
      } finally {
        setIsLoading(false);
      }
    };

    fetchLogos();
  }, []); // El array vacío asegura que esto se ejecute solo una vez al montar

  if (isLoading) {
    return <section className={styles.logosSection}><div className={styles.container}><p>Cargando logos...</p></div></section>;
  }

  if (error) {
    return <section className={styles.logosSection}><div className={styles.container}><p>Error al cargar logos: {error.message}</p></div></section>;
  }

  if (logos.length === 0) {
     return null; // No renderizar la sección si no hay logos
  }

  return (
    <section className={styles.logosSection}>
      <div className={styles.container}>
        <h2 className={styles.heading}>Verificado por las marcas más dinámicas en mercados emergentes</h2>
        <div className={styles.logosGrid}>
          {logos.map(logo => (
            <div key={logo.id} className={styles.logoItem}>
              {/* 
                Nota: Las imágenes de los logos (ej. /media/cbEicR4ACX09YVSUwmWMzNhvtMerJ5iv.png)
                no se muestran aquí porque los archivos .png/.jpg con esos nombres de archivo
                no están disponibles localmente en el directorio client/public/media/.

                Si obtienes los archivos de imagen, colócalos en client/public/media/ 
                y descomenta la línea de abajo para mostrar las imágenes en lugar del texto.
              */}
              {/* <img src={`/media/${logo.filename}`} alt={`Logo ${logo.id}`} className={styles.logoImage} /> */}

              {/* Mostrando el nombre del logo o un texto placeholder como alternativa */}
              <span className={styles.logoText}>{logo.name || `Logo ${logo.id}`}</span>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default LogosSection;
